class Message{constructor(){}init(){this.addTemplate(),this.events()}events(){document.addEventListener("DOMContentLoaded",()=>{this.onOpenTextClick(),this.onCloseMessageClick()})}onOpenTextClick(){document.querySelector("ms-box-icon").addEventListener("click",e=>{this.openText()})}onCloseMessageClick(){document.querySelector("ms-close-button").addEventListener("click",e=>{this.closeMessage()})}open(e){this.o=Object.assign({},this.defaults(),e),document.querySelector("ms-box").dataset.open="",document.querySelector("ms-box").dataset.type=this.o.type,this.o.openText?document.querySelector("ms-box").dataset.openText="":delete document.querySelector("ms-box").dataset.openText,document.querySelector("ms-box-text").innerHTML=this.o.text,this.autohide(),this.o.openText&&this.openText()}autohide(){this.o.autohide&&window.setTimeout(()=>{delete document.querySelector("ms-box").dataset.open},1500)}defaults(e){return"success"==e?{openText:!1,autohide:!0}:{openText:!0,autohide:!1}}openText(){document.querySelector("ms-box").dataset.openText=""}closeMessage(){delete document.querySelector("ms-box").dataset.open}addTemplate(){document.querySelector("#message-template")||document.body.insertAdjacentHTML("beforeend",this.template())}template(){return"\n      <ms-box>\n        <ms-box-icon></ms-box-icon>\n          <ms-box-text></ms-box-text>\n        <ms-close-button></ms-close-button>\n      </ms-box>\n    "}}